(function(angular) {
    angular.module("frictionless.app", [
        "frictionless.app.routes",
        "frictionless.app.templates"]);

})(angular);
(function(angular) {
    //routing
    var routes = angular.module("frictionless.app.routes", ["ui.router"]);

    var configuration = function ($stateProvider, $urlRouterProvider) {

        $stateProvider.state("home", {
            url: "/",
            templateUrl : "home.html",
            controller : "homeCtrl"
        });

        $urlRouterProvider.otherwise("/");
    };

    routes.config(configuration);

    configuration.$inject = ["$stateProvider", "$urlRouterProvider"];

})(angular);
(function(angular) {

    var homeCtrl = function($scope) {
        $scope.Date = new Date();
    };

    angular.module("frictionless.app")
        .controller("homeCtrl", ["$scope", homeCtrl]);
})(angular);